from django.db import models
from django.contrib import admin

class StreamItem(models.Model):

    user = models.ForeignKey('auth.User', related_name='+')
    friend_content = models.TextField()
    user_content = models.TextField()
    user_exclude = models.TextField(default='')
    created_at = models.DateTimeField()

    class Meta:
        ordering = ['-created_at']

class StreamItemAdmin(admin.ModelAdmin):
    list_display = ['user','friend_content','user_content','created_at']

admin.site.register(StreamItem,StreamItemAdmin)
