from .models import StreamItem


class SingleTableDatabaseBackend(object):

    def add_stream_item(self, users, friend_content, user_content, user_exclude, created_at):
        for user in users:
            StreamItem.objects.create(user=user, friend_content=friend_content, user_content=user_content, user_exclude=user_exclude, created_at=created_at)

    def get_stream_items(self, user):
        return StreamItem.objects.filter(user=user)
